import React, { useEffect, useRef } from 'react';
import Resume from '../resume/Resume.tsx';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // import the styles

const modules = {
  toolbar: [
    ['code']
  ]
};

export default function App() {
  const [value, setValue] = useState('');
  const reactQuillRef = useRef<ReactQuill | null>(null); // Create a ref to store the ReactQuill component

  useEffect(() => {
    if (reactQuillRef.current) {
      const quillInstance = reactQuillRef.current.getEditor(); // Obtain Quill instance
      quillInstance.format('code-block', true); // Apply 'code-block' format
    }
  }, []);

  return (
    <div className="App">
       <ReactQuill
      ref={reactQuillRef}
      value={value}
      onChange={setValue}
      modules={modules}
    />
      <Resume/>
    </div>
  );
}
