import React from "react";
import "./Resume.css";
import he from "he";


interface ResumeProps {
  html: string;
}

export default function Resume(props: ResumeProps) {
  const myRef = React.createRef();
  const decodedHtml = he.decode(props.html);

  const printDocument = () => {
    const input = myRef.current;
    html2pdf().from(input).save();
  }

  return (
    <div>
    <button onClick={printDocument}>Download as PDF</button>
    <div className="resumeWindow">
      <header>
        <h1>JEFFREY VAN OOSTERWIJK</h1>
        <p>
          jeffrey.oosterwijk@hotmail.com •+31 (0)6 40121748 • <a href="https://www.linkedin.com/in/jeffrey-van-oosterwijk-2b9154187/">linkedIn</a> • The
          Hague, The Netherlands
        </p>
        <h2>SKILLS</h2>
        <div className="skills">
          <ul>
            <li>tech principles: Tech principles: Event-driven architecture, Object-oriented programming, microservices, SOLID</li>
            <li></li>
          </ul>
        </div>
      </header>
    </div>
    </div>
  );

