import React, { ReactSVGElement, useEffect, useState }  from 'react';
import Resume from '../resume/Resume';
import { Editor, EditorState, ContentState, Modifier, getDefaultKeyBinding, CompositeDecorator } from 'draft-js';
import htmlToDraft from 'html-to-draftjs';
import './App.css';
import { htmlFile } from './template';
import { ContentBlock } from 'draft-js';
import ReactPDF, { Page, Document, View, Text, render, PDFDownloadLink } from '@react-pdf/renderer';

const findWithRegex = (contentBlock: ContentBlock, callback: (begin: number, end: number) => void) => {
  const text = contentBlock.getText();
  const regex = RegExp(/<[^>]+?>/g);
  let match: RegExpExecArray | null;
  while ((match = regex.exec(text)) !== null) {
    callback(match.index, match.index + match[0].length + 1);
  }
};

const HighlightedHTMLTag = (props: { children: any}) => {
  return <span className="highlight">{props.children}</span>;
};

const decorator = new CompositeDecorator([
  {
    strategy: findWithRegex,
    component: HighlightedHTMLTag
  }
]);


export default function App() {
  const [html, setHtml] = useState('');
  const [editorState, setEditorState] = useState<EditorState>(
    EditorState.createWithContent(
      ContentState.createFromBlockArray(
        htmlToDraft("").contentBlocks
      ),
      decorator
    )
  );

  useEffect(() => {
    const html = htmlFile;
    const content: ContentState = ContentState.createFromText(html);
    setEditorState(EditorState.push(editorState, content, 'insert-characters'));
    setHtml(html.replaceAll('\n', ''));
  },[]);

  const handleChange = (state: EditorState) => {
    setEditorState(state);
    setHtml(state.getCurrentContent().getPlainText());
  };

  const handleKeyCommand = (command: string, editorState: EditorState) => {
    if (command === 'split-block') {
      const selection = editorState.getSelection();
      const currentContent = editorState.getCurrentContent();
      const block = currentContent.getBlockForKey(selection.getStartKey()).getText();
      let howManySpaces: number = 0;
      for(let i = 0; i < block.length; i++) {
        if (block[i] !== ' ') {
          howManySpaces = i;
          break;
        }
      }
      const newContentState = Modifier.replaceText(
        editorState.getCurrentContent(),
        editorState.getSelection(),
        '\n' + ' '.repeat(howManySpaces)
      );
      setEditorState(EditorState.push(editorState, newContentState, 'insert-characters'));
      return 'handled';
    }

    if (command === 'tab') {
      const newContentState = Modifier.replaceText(
        editorState.getCurrentContent(),
        editorState.getSelection(),
        '  ' // Two spaces
      );
      setEditorState(EditorState.push(editorState, newContentState, 'insert-characters'));
      return 'handled';
    }
    if (command === 'shift-tab') {
      const selection = editorState.getSelection();
      const currentContent = editorState.getCurrentContent();
      const block = currentContent.getBlockForKey(selection.getStartKey());
      const start = selection.getStartOffset();
      if (block.getText().slice(start - 2, start) === '  ') {
        const newContentState = Modifier.replaceText(
          currentContent,
          selection.merge({ anchorOffset: start - 2, focusOffset: start }),
          ''
        );
        setEditorState(EditorState.push(editorState, newContentState, 'delete-character'));
      }
      return 'handled';
    }
    return 'not-handled';
  };

  let MyDocument = () => (
    <Document>
    <Page size="A4" >
      <View>
        <Text>Section #1</Text>
      </View>
      <View>
        <Text>Section #2</Text>
      </View>
    </Page>
  </Document>
  );

  const test =  () => { 

    const t = html.split("\n");

    const test = React.createElement("text", {}, "ba");


    const children: ReactSVGElement[] = t.map((line) => (
      React.createElement("text", {}, "ab")));

    MyDocument = () => (
      <Document>
        {test}
        {children}
        </Document>
    );
  }





  const keyBindingFn = (event: React.KeyboardEvent<{}>) => {
    if (event.key === "Tab" /* Tab */) {
      return event.shiftKey ? 'shift-tab' : 'tab';
    }
    return getDefaultKeyBinding(event);
  };

  return (
    <div className="app">
      <div className="content">
      <div>
      <PDFDownloadLink document={<MyDocument />} fileName="output.pdf">
      {({ blob, url, loading, error }) =>
        loading ? 'Loading document...' : 'Download now!'
      }
    </PDFDownloadLink>
      </div>
        <div className='myEditor'>
          <Editor
            editorState={editorState}
            handleKeyCommand={handleKeyCommand}
            onChange={handleChange} 
            keyBindingFn={keyBindingFn}
          />
        </div>
        <Resume html={html}/>
      </div>
    </div>
  );
}
